/// <reference path="./online-streaming-provider.d.ts" />

class Provider {
  private baseUrl = "https://anicrush.to";

  getSettings(): Settings {
    return {
      episodeServers: ["Southcloud-1", "Southcloud-2", "Southcloud-3"],
      supportsDub: true,
    };
  }

  async search(query: SearchOptions): Promise<SearchResult[]> {
    // --- normalize helpers ---
    const normalize = (title) => {
      return (title || "")
        .toLowerCase()
        .replace(/(season|cour|part)/g, "") // strip keywords
        .replace(/\d+(st|nd|rd|th)/g, (m) => m.replace(/st|nd|rd|th/, "")) // remove ordinal suffixes
        .replace(/[^a-z0-9]+/g, "") // remove non-alphanumeric
        .replace(/(?<!i)ii(?!i)/g, "2"); // replace II with 2
    };

    const normalizeTitle = (title) => {
      return (title || "")
        .toLowerCase()
        .replace(/(season|cour|part|uncensored)/g, "") // strip keywords
        .replace(/\d+(st|nd|rd|th)/g, (m) => m.replace(/st|nd|rd|th/, "")) // remove ordinal suffixes
        .replace(/[^a-z0-9]+/g, ""); // remove non-alphanumeric
    };

    const normalizeDate = (dateStr: string) => {
      if (!dateStr) return null;
      const months: Record<string, string> = {
        Jan: "01", Feb: "02", Mar: "03", Apr: "04",
        May: "05", Jun: "06", Jul: "07", Aug: "08",
        Sep: "09", Oct: "10", Nov: "11", Dec: "12",
      };
      const m = dateStr.match(/([A-Za-z]+)\s+\d{1,2},\s*(\d{4})/);
      if (!m) return null;
      return { year: parseInt(m[2]), month: parseInt(months[m[1]]) };
    };

    const levenshteinSimilarity = (a, b) => {
      const lenA = a.length;
      const lenB = b.length;
      const dp = Array.from({ length: lenA + 1 }, () => new Array(lenB + 1).fill(0));

      for (let i = 0; i <= lenA; i++) dp[i][0] = i;
      for (let j = 0; j <= lenB; j++) dp[0][j] = j;

      for (let i = 1; i <= lenA; i++) {
        for (let j = 1; j <= lenB; j++) {
          if (a[i - 1] === b[j - 1]) dp[i][j] = dp[i - 1][j - 1];
          else dp[i][j] = 1 + Math.min(dp[i - 1][j], dp[i][j - 1], dp[i - 1][j - 1]);
        }
      }

      const distance = dp[lenA][lenB];
      const maxLen = Math.max(lenA, lenB);
      return 1 - distance / maxLen; // similarity in range [0,1]
    };

    const start = query.media.startDate;
    const targetNormJP = normalize(query.media.romajiTitle);
    const targetNorm = query.media.englishTitle
      ? normalize(query.media.englishTitle)
      : targetNormJP;

    const fetchMatches = async (url: string) => {
      const html = await fetch(url, {
        headers: {
          "User-Agent": "Mozilla/5.0",
          "Accept": "application/json",
          "Referer": this.baseUrl + "/",
          "Origin": this.baseUrl,
          "X-Site": "anicrush",
        },
      }).then(res => res.json());

      return html.result?.movies.map((movie: any) => ({
        id: movie.id,
        pageUrl: movie.slug,
        title: movie.name_english ? movie.name_english : movie.name,
        titleJP: movie.name,
        normTitleJP: normalize(movie.name),
        normTitle: normalize(movie.name_english ? movie.name_english : movie.name),
        dub: movie.has_dub,
        startDate: normalizeDate(movie.aired_from),
      })) ?? [];
    };

    // Base search
    const url = `https://api.anicrush.to/shared/v2/movie/list?keyword=${encodeURIComponent(query.query)}&limit=48&page=1`;
    const matches = await fetchMatches(url);
    if (!matches.length) return [];

    // If dub requested, filter only dub-capable matches
    if (query.dub) matches = matches.filter(m => m.dub);

    // Filter results prioritizing from -> match title & start year/month -> match title & start year
    let filtered = matches.filter(m => {
      const titleMatch =
        m.normTitle === targetNorm ||
        m.normTitleJP === targetNormJP;

      const dateMatch =
        (m.startDate?.year === start?.year) &&
        (m.startDate?.month === start?.month);

      return titleMatch && dateMatch;
    });

    if (!filtered.length) {
      filtered = matches.filter(m => {
        const titleMatch =
          m.normTitle === targetNorm ||
          m.normTitleJP === targetNormJP;

        const dateMatch =
          (m.startDate?.year === start?.year)
        return titleMatch && dateMatch;
      });
    }

    if (!filtered.length) {
      filtered = matches.filter(m => {
        const titleMatch =
          m.normTitle.includes(targetNorm) ||
          m.normTitleJP.includes(targetNormJP) ||
          targetNorm.includes(m.normTitle) ||
          targetNormJP.includes(m.normTitleJP) ||
          levenshteinSimilarity(m.normTitle, targetNorm) > 0.7 ||
          levenshteinSimilarity(m.normTitleJP, targetNormJP) > 0.7;

        const dateMatch =
          (m.startDate?.year === start?.year) &&
          (m.startDate?.month === start?.month);

        return titleMatch && dateMatch;
      });
    }

    if (!filtered.length) {
      filtered = matches.filter(m => {
        const titleMatch =
          m.normTitle.includes(targetNorm) ||
          m.normTitleJP.includes(targetNormJP) ||
          targetNorm.includes(m.normTitle) ||
          targetNormJP.includes(m.normTitleJP) ||
          levenshteinSimilarity(m.normTitle, targetNorm) > 0.7 ||
          levenshteinSimilarity(m.normTitleJP, targetNormJP) > 0.7;

        const dateMatch =
          (m.startDate?.year === start?.year)
        return titleMatch && dateMatch;
      });
    }

    // Return results
    let results = filtered.map(m => ({
      id: `${m.id}/${query.dub ? "dub" : "sub"}`,
      title: m.title,
      url: `${this.baseUrl}/detail/${m.pageUrl}.${m.id}`,
      subOrDub: query.dub ? "dub" : "sub",
    }));

    if (!query.media.startDate || !query.media.startDate.year) {
      filtered = matches.filter(m => {
        const titleMatch =
          normalizeTitle(m.title) === normalizeTitle(query.query) ||
          normalizeTitle(m.titleJP) === normalizeTitle(query.query) ||
          normalizeTitle(m.title).includes(normalizeTitle(query.query)) ||
          normalizeTitle(m.titleJP).includes(normalizeTitle(query.query)) ||
          normalizeTitle(query.query).includes(normalizeTitle(m.title)) ||
          normalizeTitle(query.query).includes(normalizeTitle(m.titleJP));
        return titleMatch;
      });
      filtered.sort((a, b) => {
        const A = normalizeTitle(a.title);
        const B = normalizeTitle(b.title);

        // 1) Sort by length
        if (A.length !== B.length) {
          return A.length - B.length;
        }

        // 2) If lengths match, sort alphabetically
        return A.localeCompare(B);
      });
      results = filtered.map(m => ({
        id: `${m.id}/${query.dub ? "dub" : "sub"}`,
        title: m.title,
        url: `${this.baseUrl}/detail/${m.pageUrl}.${m.id}`,
        subOrDub: query.dub ? "dub" : "sub",
      }));
    }

    return results;
  }

  async findEpisodes(Id: string): Promise<Episode[]> {
    const [id, subOrDub] = Id.split("/");

    const epRes = await fetch(
      `https://api.anicrush.to/shared/v2/episode/list?_movieId=${id}`,
      {
        headers: {
          "User-Agent": "Mozilla/5.0",
          "Accept": "application/json",
          "Referer": this.baseUrl + "/",
          "Origin": this.baseUrl,
          "X-Site": "anicrush",
        },
      }
    );

    const epJson = await epRes.json();
    const episodeGroups = epJson?.result ?? {};
    const episodes: Episode[] = [];

    for (const group of Object.values(episodeGroups)) {
      if (!Array.isArray(group)) continue;

      for (const ep of group) {
        episodes.push({
          id: `${id}/${subOrDub}`,
          number: ep.number,
          title: ep.name_english,
          url: "",
        });
      }
    }

    return episodes;
  }

  async findEpisodeServer(episode: EpisodeDetails, _server: string): Promise<EpisodeServer> {
    const [id, subOrDub] = episode.id.split("/");

    const serverMap: Record<string, number> = {
      "Southcloud-1": 4,
      "Southcloud-2": 1,
      "Southcloud-3": 6,
    };

    const sv = serverMap[_server] ?? 4;

    const encryptedLinkUrl = `https://api.anicrush.to/shared/v2/episode/sources?_movieId=${id}&ep=${episode.number}&sv=${sv}&sc=${subOrDub}`;

    try {
      // Fetch encrypted link
      const res = await fetch(encryptedLinkUrl, {
        headers: {
          "User-Agent": "Mozilla/5.0",
          "Accept": "application/json",
          "Referer": this.baseUrl + "/",
          "Origin": this.baseUrl,
          "X-Site": "anicrush",
        },
      });

      const json = await res.json();
      const encryptedIframe = json?.result?.link;
      if (!encryptedIframe) throw new Error("Missing encrypted iframe link");

      // Try primary decrypter
      let decryptData = null;
      try {
        decryptData = await extractMegaCloud(encryptedIframe);
      } catch (err) {
        console.warn("Primary decrypter failed:", err);
      }

      // Fallback to ShadeOfChaos if primary fails or no valid data
      if (!decryptData) {
        console.warn("Primary decrypter failed â€” trying ShadeOfChaos fallback...");
        const fallbackRes = await fetch(
          `https://ac-api.ofchaos.com/api/anime/embed/convert/v2?embedUrl=${encodeURIComponent(encryptedIframe)}`
        );
        decryptData = await fallbackRes.json();
      }

      // Still nothing? Fail
      if (!decryptData) throw new Error("No video sources from any decrypter");

      // Get HLS or MP4 stream
      const streamSource =
        decryptData.sources.find((s: any) => s.type === "hls") ||
        decryptData.sources.find((s: any) => s.type === "mp4");

      if (!streamSource?.file) throw new Error("No valid stream file found");

      // Map subtitles
      const subtitles =
        (decryptData.tracks || [])
          .filter((t: any) => t.kind === "captions")
          .map((track: any, index: number) => ({
            id: `sub-${index}`,
            language: track.label || "Unknown",
            url: track.file,
            isDefault: !!track.default,
          }));

      // Return playable server object
      return {
        server: _server,
        headers: {
          "Referer": "https://megacloud.club/",
          "Origin": "https://megacloud.club",
          "User-Agent":
            "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/139.0.0.0 Safari/537.36 Edg/139.0.0.0",
        },
        videoSources: [
          {
            url: streamSource.file,
            type: streamSource.type,
            quality: "auto",
            subtitles,
          },
        ],
      };
    } catch (err) {
      console.warn(`Failed on ${_server}`, err);
      throw new Error(`No stream found for ${_server}`);
    }
  }
}

async function extractMegaCloud(embedUrl: string) {
  const url = new URL(embedUrl);
  const baseDomain = `${url.protocol}//${url.host}/`;

  const headers = {
    "Accept": "*/*",
    "X-Requested-With": "XMLHttpRequest",
    "Referer": baseDomain,
    "User-Agent":
      "Mozilla/5.0 (Linux; Android 10; K) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/137.0.0.0 Mobile Safari/537.36",
  };

  // Fetch embed page
  const html = await fetch(embedUrl, { headers }).then((r) => r.text());

  // Extract file ID
  const fileIdMatch = html.match(/<title>\s*File\s+#([a-zA-Z0-9]+)\s*-/i);
  if (!fileIdMatch) throw new Error("file_id not found in embed page");
  const fileId = fileIdMatch[1];

  // Extract nonce
  let nonce: string | null = null;
  const match48 = html.match(/\b[a-zA-Z0-9]{48}\b/);
  if (match48) nonce = match48[0];
  else {
    const match3x16 = [...html.matchAll(/["']([A-Za-z0-9]{16})["']/g)];
    if (match3x16.length >= 3) {
      nonce = match3x16[0][1] + match3x16[1][1] + match3x16[2][1];
    }
  }
  if (!nonce) throw new Error("nonce not found");

  // Fetch sources
  const sourcesJson = await fetch(
    `${baseDomain}embed-2/v3/e-1/getSources?id=${fileId}&_k=${nonce}`,
    { headers }
  ).then((r) => r.json());

  return {
    sources: sourcesJson.sources,
    tracks: sourcesJson.tracks || [],
    intro: sourcesJson.intro || null,
    outro: sourcesJson.outro || null,
    server: sourcesJson.server || null,
  };
}
